@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun LoginScreen(
    onLoginSuccess: () -> Unit,
    viewModel: LoginViewModel = viewModel()
) {
    val focusManager = LocalFocusManager.current
    val keyboardController = LocalSoftwareKeyboardController.current
    val usernameFocusRequester = remember { FocusRequester() }
    val passwordFocusRequester = remember { FocusRequester() }
    // 用于跟踪哪个输入框有焦点
    var activeField by remember { mutableStateOf<Field?>(null) }

    // 处理键盘操作（下一步/完成）
    val keyboardActions = KeyboardActions(
        onNext = { passwordFocusRequester.requestFocus() },
        onDone = {
            focusManager.clearFocus()
            keyboardController?.hide()
            activeField = null
        }
    )
    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(Color(0xFFF5F5F5))
            .padding(16.dp)
            .clickable(
                interactionSource = remember { MutableInteractionSource() },
                indication = null  // 禁用涟漪效果
            ) {
                // 点击屏幕任何地方时清除焦点并隐藏键盘
                focusManager.clearFocus()
                keyboardController?.hide()
                activeField = null
            },
        contentAlignment = Alignment.Center
    ) {
        Column(
            modifier = Modifier
                .widthIn(max = 400.dp)
                .padding(
                    start = 32.dp,
                    end = 32.dp,
                    bottom = 32.dp
                )
                .background(Color.White)
                .clickable(
                    indication = null,
                    interactionSource = remember { MutableInteractionSource() }) {
                    // 阻止点击表单区域时关闭键盘
                },
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = "地图应用登录",
                style = MaterialTheme.typography.headlineMedium,
                modifier = Modifier.padding(bottom = 24.dp)
            )

            OutlinedTextField(
                value = viewModel.username,
                onValueChange = { viewModel.username = it },
                label = { Text("用户名") },
                singleLine = true,
                keyboardOptions = KeyboardOptions(
                    keyboardType = KeyboardType.Text,
                    imeAction = ImeAction.Next
                ),
                keyboardActions = keyboardActions,
                modifier = Modifier
                    .fillMaxWidth()
                    .focusRequester(usernameFocusRequester)
                    .onFocusChanged { focusState ->
                        if (focusState.isFocused) {
                            activeField = Field.USERNAME
                        } else if (activeField == Field.USERNAME) {
                            activeField = null
                        }
                    }
            )

            Spacer(modifier = Modifier.height(16.dp))
            PasswordTextField(
                viewModel = viewModel,
                passwordFocusRequester = passwordFocusRequester,
            )


            Spacer(modifier = Modifier.height(24.dp))
            CustomButton(text = "登录", onClick = {  // 登录逻辑...
                if (viewModel.isValidCredentials()) {
                    onLoginSuccess()
                }
                // 登录后清除焦点
                focusManager.clearFocus()
                keyboardController?.hide() })

        }
    }

}
@Composable
fun PasswordTextField(
    viewModel: LoginViewModel, // 替换为你的ViewModel类型
    passwordFocusRequester: FocusRequester,
    modifier: Modifier = Modifier
) {
    // 控制密码是否可见的状态
    var passwordVisible by remember { mutableStateOf(false) }

    OutlinedTextField(
        value = viewModel.password,
        onValueChange = { viewModel.password = it },
        label = { Text("密码") },
        visualTransformation = if (passwordVisible) {
            VisualTransformation.None // 显示明文
        } else {
            PasswordVisualTransformation() // 显示星号
        },
        keyboardOptions = KeyboardOptions(
            keyboardType = KeyboardType.Password,
            imeAction = ImeAction.Done
        ),
        keyboardActions = KeyboardActions(
            onDone = {
                // 处理完成操作
            }
        ),
        trailingIcon = {
            // 密码可见性切换按钮
            IconButton(onClick = { passwordVisible = !passwordVisible }) {
                Icon(
                    painter = if (passwordVisible) {
                        painterResource(R.drawable.visibility_24px)
                    } else {
                        painterResource(R.drawable.visibility_off_24px)
                    },
                    contentDescription = if (passwordVisible) {
                        "隐藏密码"
                    } else {
                        "显示密码"
                    }
                )
            }
        },
        modifier = modifier
            .fillMaxWidth()
            .focusRequester(passwordFocusRequester)

    )
}


// 用于跟踪当前活动的输入字段
enum class Field {
    USERNAME, PASSWORD
}

class LoginViewModel : ViewModel() {
    var username by mutableStateOf("")
    var password by mutableStateOf("")

    fun isValidCredentials(): Boolean {
        // 这里添加实际的验证逻辑
        return username.isNotBlank() && password.length >= 6
    }
