package com.hover.app.utils

import io.ktor.client.*
import io.ktor.client.engine.cio.*
import io.ktor.client.request.*
import io.ktor.client.statement.*
import io.ktor.serialization.kotlinx.json.*
import io.ktor.client.plugins.logging.*
import io.ktor.http.HttpHeaders
import io.ktor.client.engine.android.*
import io.ktor.client.plugins.contentnegotiation.*
import io.ktor.http.ContentType
import kotlinx.serialization.json.Json
import kotlinx.serialization.Serializable


// 1. 定义正确的数据模型
@Serializable
data class PermissionResponse(
    val code: Int,
    val message: String,
    val data: List<Permission>
)

@Serializable
data class Permission(
    val description: String,
    val name: String
)
suspend fun fetchPermissions(): PermissionResponse {
    val client = HttpClient(CIO) {
        install(ContentNegotiation) {
            json(Json {
                ignoreUnknownKeys = true
                explicitNulls = false
            })
        }
    }

    return try {
        // 正确方式：使用类型参数指定期望的响应类型
        client.get("http://localhost:18080/api/auth/permissions") {
            header(HttpHeaders.Accept, ContentType.Application.Json)
            header(HttpHeaders.ContentType, ContentType.Application.Json)
        }
    } catch (e: Exception) {
        // 处理错误情况
        println("请求失败: ${e.message}")
        PermissionResponse(code = -1, message = "请求失败: ${e.message}", data = emptyList())
    } finally {
        client.close()
    }as PermissionResponse
}
suspend fun main() {
    val response = fetchPermissions()
    println("响应码: ${response.code}")
    println("消息: ${response.message}")
    println("权限列表:")
    response.data.forEach { permission ->
        println("${permission.name}: ${permission.description}")
    }
}

package com.hover.app.utils

import io.ktor.client.*
import io.ktor.client.call.*
import io.ktor.client.engine.android.*
import io.ktor.client.plugins.*
import io.ktor.client.plugins.contentnegotiation.*
import io.ktor.client.plugins.logging.*
import io.ktor.client.request.*
import io.ktor.client.statement.*
import io.ktor.http.*
import io.ktor.serialization.kotlinx.json.*
import kotlinx.serialization.*
import kotlinx.serialization.json.Json

// 基础响应模型 - 添加 @Serializable 注解
@Serializable
data class BaseResponse<T>(
    val code: Int,
    val message: String,
    val data: T
)

// 权限数据模型
@Serializable
data class Permission(
    val description: String,
    val name: String
)

// 网络请求工具对象
object NetworkClient {
    // 创建全局HttpClient实例
    val client = HttpClient(Android) {
        // 安装日志插件
        install(Logging) {
            logger = Logger.DEFAULT
            level = LogLevel.HEADERS
            filter { request -> request.url.host.contains("ktor.io") }
            sanitizeHeader { header -> header == HttpHeaders.Authorization }
        }

        // 安装内容协商插件
        install(ContentNegotiation) {
            json(Json {
                ignoreUnknownKeys = true
                explicitNulls = false
                encodeDefaults = true
                isLenient = true
                // 启用对泛型的支持
                useArrayPolymorphism = false
            })
        }

        // 安装默认请求配置
        defaultRequest {
            contentType(ContentType.Application.Json)
            accept(ContentType.Application.Json)
        }
    }

    // 通用GET请求方法 (修复序列化问题)
    suspend inline fun <reified T> get(
        url: String,
        headers: Map<String, String> = emptyMap()
    ): T {
        val response = client.get(url) {
            headers.forEach { (key, value) ->
                header(key, value)
            }
        }

        // 先获取原始响应文本用于调试
        val rawResponse = response.bodyAsText()
        println("原始响应: $rawResponse")

        try {
            // 尝试直接反序列化
            return response.body()
        } catch (e: SerializationException) {
            println("反序列化失败: ${e.message}")
            // 尝试手动解析
            return Json.decodeFromString(rawResponse)
        }
    }

    // 关闭客户端
    fun close() {
        client.close()
    }
}

// 权限服务接口 (添加显式类型声明)
object AuthService {
    private const val PERMISSIONS_URL = "http://localhost:18080/api/auth/permissions"

    suspend fun getPermissions(): BaseResponse<List<Permission>> {
        // 显式指定泛型类型
        return NetworkClient.get<BaseResponse<List<Permission>>>(PERMISSIONS_URL)
    }
}

