    // 用于跟踪哪个输入框有焦点
    var activeField by remember { mutableStateOf<Field?>(null) }

    // 处理键盘操作（下一步/完成）
    val keyboardActions = KeyboardActions(
        onNext = { passwordFocusRequester.requestFocus() },
        onDone = {
            focusManager.clearFocus()
            keyboardController?.hide()
            activeField = null
        }
    )
    Box(
