package com.hover.app.utils

import android.util.Log
import io.ktor.client.*
import io.ktor.client.call.*
import io.ktor.client.engine.android.*
import io.ktor.client.plugins.*
import io.ktor.client.plugins.contentnegotiation.*
import io.ktor.client.plugins.logging.*
import io.ktor.client.request.*
import io.ktor.client.statement.*
import io.ktor.http.*
import io.ktor.serialization.kotlinx.json.*
import kotlinx.coroutines.*
import kotlinx.serialization.*
import kotlinx.serialization.json.*
import java.io.IOException

// 基础响应模型
@Serializable
data class BaseResponse<T>(
    val code: Int,
    val message: String,
    val data: T
)

// 权限数据模型
@Serializable
data class Permission(
    val description: String,
    val name: String
)

// 安全网络客户端
object SafeNetworkClient {
    // 使用协程作用域管理客户端生命周期
    private var client: HttpClient? = null

    // 获取或创建 HttpClient 实例
    private fun getClient(): HttpClient {
        return client ?: createHttpClient().also {
            client = it
        }
    }

    // 创建 HttpClient
    private fun createHttpClient(): HttpClient {
        return HttpClient(Android) {
            // 使用简单日志而不是 Logback
            install(Logging) {
                logger = object : Logger {
                    override fun log(message: String) {
                        Log.d("Ktor", message)
                    }
                }
                level = LogLevel.HEADERS
            }
            engine {
                // this: AndroidEngineConfig
                connectTimeout = 100_000
                socketTimeout = 100_000
//                proxy = Proxy(Proxy.Type.HTTP, InetSocketAddress("localhost", 8080))
            }

            // 内容协商配置
            install(ContentNegotiation) {
                json(Json {
                    ignoreUnknownKeys = true
                    explicitNulls = false
                    isLenient = true
                })
            }

            // 默认请求配置
            defaultRequest {
                contentType(ContentType.Application.Json)
                accept(ContentType.Application.Json)
                header("Accept-Charset", "UTF-8")
            }

            // 异常处理
            HttpResponseValidator {
                validateResponse { response ->
                    val statusCode = response.status.value
//                    when (statusCode) {
//                        in 300..399 -> throw RedirectResponseException(response)
//                        in 400..499 -> throw ClientRequestException(response)
//                        in 500..599 -> throw ServerResponseException(response)
//                    }
                }
            }
        }
    }

    // 安全执行网络请求
    suspend fun <T> safeRequest(
        request: suspend HttpClient.() -> T
    ): Result<T> {
        return try {
            Result.success(getClient().request())
        } catch (e: Exception) {
            Log.e("Network", "请求失败: ${e.message}", e)
            Result.failure(e)
        }
    }

    // 关闭客户端
    fun close() {
        client?.close()
        client = null
    }
}

// 认证服务
object AuthService {
    // 使用模拟器专用地址
    private const val PERMISSIONS_URL = "http://localhost:18080/api/auth/permissions"

    suspend fun getPermissions(): Result<BaseResponse<List<Permission>>> {
        println("正在请求权限数据...")
        return SafeNetworkClient.safeRequest {
            get(PERMISSIONS_URL) {
                timeout {
                    requestTimeoutMillis = 15000
                }
            }.body()
        }
    }
}

// 登录功能
suspend fun login() {
    val result = AuthService.getPermissions()

    result.fold(
        onSuccess = { response ->
            Log.d("Login", "✅ 请求成功! 状态: ${response.code}, 消息: ${response.message}")
            Log.d("Login", "️ 权限列表 (${response.data.size} 项):")
            response.data.forEachIndexed { index, permission ->
                Log.d("Login", "${index + 1}. ${permission.name} - ${permission.description}")
            }
        },
        onFailure = { error ->
            Log.e("Login", "❌ 请求失败", error)
            when (error) {
                is ClientRequestException ->
                    Log.e("Login", "客户端错误: ${error.response.status}")
                is ServerResponseException ->
                    Log.e("Login", "服务器错误: ${error.response.status}")
                is IOException ->
                    Log.e("Login", "网络错误: ${error.message}")
                else ->
                    Log.e("Login", "未知错误: ${error.message}")
            }
        }
    )

    // 不在 finally 中关闭客户端，保持连接重用
}

