import androidx.compose.animation.core.* import androidx.compose.foundation.Image import androidx.compose.foundation.background import androidx.compose.foundation.layout.* import androidx.compose.material3.CircularProgressIndicator import androidx.compose.material3.MaterialTheme import androidx.compose.material3.Text import androidx.compose.runtime.* import androidx.compose.ui.Alignment import androidx.compose.ui.Modifier import androidx.compose.ui.draw.alpha import androidx.compose.ui.draw.scale import androidx.compose.ui.graphics.Color import androidx.compose.ui.graphics.ColorFilter import androidx.compose.ui.res.painterResource import androidx.compose.ui.res.stringResource import androidx.compose.ui.text.font.FontWeight import androidx.compose.ui.text.style.TextAlign import androidx.compose.ui.unit.dp import androidx.compose.ui.unit.sp import com.hover.app.R import kotlinx.coroutines.delay
@Composable fun SplashScreen( onLoadingComplete: () -> Unit, modifier: Modifier = Modifier ) { // 控制动画状态 var animationState by remember { mutableStateOf(0f) }
// 动画控制器 val infiniteTransition = rememberInfiniteTransition() val pulse by infiniteTransition.animateFloat( initialValue = 0.9f, targetValue = 1.1f, animationSpec = infiniteRepeatable( animation = tween(800, easing = FastOutSlowInEasing), repeatMode = RepeatMode.Reverse ) )
// 模拟加载完成 LaunchedEffect(Unit) { // 启动动画 animate( initialValue = 0f, targetValue = 1f, animationSpec = tween(1200, easing = FastOutSlowInEasing) ) { value, _ -> animationState = value }
// 模拟加载过程 delay(2000)
// 加载完成后回调 onLoadingComplete() }
Box( modifier = modifier .fillMaxSize() .background(MaterialTheme.colorScheme.primary), contentAlignment = Alignment.Center ) { Column( horizontalAlignment = Alignment.CenterHorizontally, verticalArrangement = Arrangement.Center, modifier = Modifier .fillMaxWidth() .padding(horizontal = 32.dp) ) { // Logo 图片 - 带缩放动画 Image( painter = painterResource(id = R.mipmap.ic_launcher), contentDescription = "App Logo", modifier = Modifier .size(180.dp) .scale(0.8f + animationState * 0.2f) // 缩放动画 .alpha(animationState) // 淡入效果 )
Spacer(modifier = Modifier.height(24.dp))
// 应用名称 - 带淡入动画 Text( text = stringResource(id = R.string.app_name), color = Color.White, fontSize = 28.sp, fontWeight = FontWeight.Bold, modifier = Modifier.alpha(animationState), textAlign = TextAlign.Center )
Spacer(modifier = Modifier.height(24.dp))
// 加载进度条 - 带脉冲动画 CircularProgressIndicator( color = Color.White, trackColor = Color.White.copy(alpha = 0.2f), modifier = Modifier .size(48.dp) .scale(pulse) // 脉冲动画 ) }
// 版本信息 - 底部对齐 Text( text = stringResource(id = R.string.version_info), color = Color.White.copy(alpha = 0.6f), fontSize = 12.sp, modifier = Modifier .align(Alignment.BottomCenter) .padding(bottom = 24.dp) .alpha(animationState) // 淡入效果 ) } }

package com.hover.app.pages

import androidx.compose.animation.core.*
import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.material3.CircularProgressIndicator
import androidx.compose.material3.MaterialTheme
import androidx.compose.material3.Text
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.alpha
import androidx.compose.ui.draw.scale
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.graphics.ColorFilter
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.res.stringResource
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.hover.app.R
import kotlinx.coroutines.delay

@Composable
fun SplashScreen(
    onLoadingComplete: () -> Unit,
    modifier: Modifier = Modifier
) {
    // 控制动画状态
    var animationState by remember { mutableStateOf(0f) }

    // 动画控制器
    val infiniteTransition = rememberInfiniteTransition()
    val pulse by infiniteTransition.animateFloat(
        initialValue = 0.9f,
        targetValue = 1.1f,
        animationSpec = infiniteRepeatable(
            animation = tween(800, easing = FastOutSlowInEasing),
            repeatMode = RepeatMode.Reverse
        )
    )

    // 模拟加载完成
    LaunchedEffect(Unit) {
        // 启动动画
        animate(
            initialValue = 0f,
            targetValue = 1f,
            animationSpec = tween(1200, easing = FastOutSlowInEasing)
        ) { value, _ -> animationState = value }

        // 模拟加载过程
        delay(2000)

        // 加载完成后回调
        onLoadingComplete()
    }

    Box(
        modifier = modifier
            .fillMaxSize()
            .background(MaterialTheme.colorScheme.primary),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.Center,
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 32.dp)
        ) {
            // Logo 图片 - 带缩放动画
//            Image(
//                painter = painterResource(id = R.mipmap.ic_launcher),
//                contentDescription = "App Logo",
//                modifier = Modifier
//                    .size(180.dp)
//                    .scale(0.8f + animationState * 0.2f) // 缩放动画
//                    .alpha(animationState) // 淡入效果
//            )

            Spacer(modifier = Modifier.height(24.dp))

            // 应用名称 - 带淡入动画
            Text(
                text = stringResource(id = R.string.app_name),
                color = Color.White,
                fontSize = 28.sp,
                fontWeight = FontWeight.Bold,
                modifier = Modifier.alpha(animationState),
                textAlign = TextAlign.Center
            )

            Spacer(modifier = Modifier.height(24.dp))

            // 加载进度条 - 带脉冲动画
            CircularProgressIndicator(
                color = Color.White,
                trackColor = Color.White.copy(alpha = 0.2f),
                modifier = Modifier
                    .size(48.dp)
                    .scale(pulse) // 脉冲动画
            )
        }

        // 版本信息 - 底部对齐
        Text(
            text = stringResource(id = R.string.version_info),
            color = Color.White.copy(alpha = 0.6f),
            fontSize = 12.sp,
            modifier = Modifier
                .align(Alignment.BottomCenter)
                .padding(bottom = 24.dp)
                .alpha(animationState) // 淡入效果
        )
    }
}

