}

// 设置抽屉内容
@Composable
fun SettingsDrawerContent(
) {
    // 添加垂直滚动支持
    val scrollState = rememberScrollState()
    var selectedSetting by remember { mutableStateOf("船速") }
    Row(
        modifier = Modifier
            .fillMaxHeight()
            .width(300.dp)
            .background(MaterialTheme.colorScheme.surface)
    ) {
        Column(
            modifier = Modifier
                .fillMaxHeight()
                .width(50.dp)
                .verticalScroll(scrollState)
                .background(Color(0x88040408)) // 添加背景色
                .clickable(enabled = true) {}, // 消费点击事件
        ) {
            val menuItems = listOf("船速", "安全", "地图", "通用", "关于")
            val selectedItem = remember { mutableStateOf(4) } // 假设 "关于" 是选中的

            Column {
                menuItems.forEachIndexed { index, item ->
                    SettingCategoryItem(
                        title = item,
                        isSelected = index == selectedItem.value,
                        onClick = {
                            selectedItem.value = index
                            selectedSetting = item
                        }
                    )
                }
            }


        }

        // 右侧：具体设置内容
        Column(
            modifier = Modifier
                .weight(1f)
                .fillMaxHeight()
                .background(
                    Color.Black.copy(alpha = 0.80f)
                )
                .verticalScroll(rememberScrollState())
        ) {

            // 根据选中的设置项显示不同的内容
            when (selectedSetting) {
                "船速" -> ShipSpeed(
                    onConfirm = { /* 确认船速 */ }
                )
                "安全" -> LayerControlSettings()
//                "地图" -> MapTypeSettings()
//                "通用" -> PrivacySettings()
//                "关于" -> AccountSettings()
//                "关于应用" -> AboutAppSettings()
            }

        }
    }

}
@Composable
fun CustomTextField(
    value: String,
    onValueChange: (String) -> Unit,
    placeholder: @Composable () -> Unit,
    modifier: Modifier = Modifier
) {
    BasicTextField(
        value = value,
        onValueChange = onValueChange,
        modifier = modifier
            .border(1.dp, Color.White, RoundedCornerShape(4.dp))
            .padding(4.dp),
        decorationBox = { innerTextField ->
            Row(
                verticalAlignment = Alignment.CenterVertically
            ) {
                if (value.isEmpty()) {
                    placeholder()
                }
                innerTextField()
            }
        },
        keyboardOptions = KeyboardOptions.Default.copy(
            keyboardType = KeyboardType.Text
        )
    )
}

@Composable
fun ShipSpeed(
    onConfirm: () -> Unit,
    viewModel: MapViewModel = viewModel()
) {
    val focusManager = LocalFocusManager.current
    val keyboardController = LocalSoftwareKeyboardController.current
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .background(Color(0xFF040404))
            .padding(8.dp)
            .clickable(indication = null, interactionSource = remember { MutableInteractionSource() }) {
                // 点击屏幕任何地方时清除焦点并隐藏键盘
                focusManager.clearFocus()
                keyboardController?.hide()
            },
        horizontalArrangement = Arrangement.SpaceBetween,
        verticalAlignment = Alignment.CenterVertically

    ) {
        Text(
            text = "自动速度",
            color = Color.White,
            fontWeight = FontWeight.Normal
        )

        CustomTextField(
            value = "",
            onValueChange = {},
            placeholder = {
                Text(
                    text = "自动船速",
                    color = Color.White,
                    fontWeight = FontWeight.Normal
                )
            },
            modifier = Modifier
                .width(80.dp)
                .height(30.dp)
        )


        Text(
            text = "m/s",
            color = Color.White,
            fontWeight = FontWeight.Normal
        )
        CustomButton(text = "确定", onClick = { onConfirm() })
    }
}

@Composable
fun CustomButton(
    text: String, // 按钮上显示的文本内容
    onClick: () -> Unit, // 按钮点击事件的回调函数
    modifier: Modifier = Modifier, // 用于自定义按钮的修饰符，默认为 Modifier
    shape: Shape = RoundedCornerShape(6.dp), // 按钮的形状，默认为圆角矩形
    containerColor: Color = Color(0xFF0066CC), // 按钮的背景颜色，默认为蓝色
    contentColor: Color = Color.White, // 按钮的文本颜色，默认为白色
    contentPadding: PaddingValues = PaddingValues(0.dp), // 按钮内容的内边距，默认为0
    innerPadding: PaddingValues = PaddingValues(4.dp) // 文本的内边距，默认为4.dp
) {
    Button(
        onClick = onClick,
        modifier = modifier
            .wrapContentSize()
            .then(modifier),
        shape = shape,
        colors = ButtonDefaults.buttonColors(
            containerColor = containerColor,
            contentColor = contentColor
        ),
        contentPadding = contentPadding
    ) {
        Text(
            text = text,
            color = contentColor,
            fontWeight = FontWeight.Normal,
            modifier = Modifier.padding(innerPadding)
        )
    }
}


@Composable
fun AddMarker(
    point: Point,
    onPointClicked: (PointAnnotation) -> Unit,
    onPointUpdated: (Point) -> Unit
) {
    val marker = rememberIconImage(
        key = R.drawable.ic_blue_marker,
        painter = painterResource(R.drawable.ic_blue_marker)
    )

    PointAnnotation(point = point) {
        iconImage = marker
        interactionsState.onDragged { event ->
            // 更新点位置
            val newPoint = Point.fromLngLat(event.point.longitude(), event.point.latitude())
            onPointUpdated(newPoint)
            true
        }.onClicked { event ->
            onPointClicked(event)
            true
        }
            .also {
                it.isDraggable = true
            }
    }
}

@Composable
fun SettingCategoryItem(
    title: String,
    isSelected: Boolean,
    onClick: () -> Unit
) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .height(56.dp)
            .clickable { onClick() },
        verticalAlignment = Alignment.CenterVertically, // 显式设置垂直居中
        horizontalArrangement = Arrangement.Center // 水平居中
    ) {
        Box(
            modifier = Modifier
                .width(3.dp)
                .fillMaxHeight()
                .background(
                    if (isSelected) Color(0xFF0066CC) else Color.Transparent
                )
                .align(Alignment.CenterVertically) // 确保指示条垂直居中
        )
        Text(
            text = title,
            modifier = Modifier
                .weight(1f)
                .align(Alignment.CenterVertically)
                .padding(start = 8.dp),
            color = if (isSelected) Color(0xFF0066CC) else Color.White,
            fontWeight = if (isSelected) FontWeight.Bold else FontWeight.Normal,
        )

    }
}

@Composable
fun MapTypeSettings() {
    Column {
        Text(
            text = "选择地图显示样式",
            style = MaterialTheme.typography.bodyMedium,
            color = Color.White
        )

        Spacer(modifier = Modifier.height(16.dp))

        // 船速选项
        MapTypeOption("标准地图", true)
        MapTypeOption("卫星地图", false)
        MapTypeOption("地形地图", false)
        MapTypeOption("夜间模式", false)
    }
}

@Composable
fun MapTypeOption(name: String, isSelected: Boolean) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(vertical = 8.dp)
            .clickable { /* 选择船速 */ },
        verticalAlignment = Alignment.CenterVertically
    ) {
        RadioButton(
            selected = isSelected,
            onClick = { /* 选择船速 */ }
        )

        Spacer(modifier = Modifier.width(16.dp))

        Text(
            text = name,
            style = MaterialTheme.typography.bodyLarge,
            color = Color.White
        )
    }
}

@Composable
fun LayerControlSettings() {
    Column {
        Text(
            text = "管理地图图层",
            style = MaterialTheme.typography.bodyMedium,
            color = MaterialTheme.colorScheme.onSurfaceVariant
        )

        Spacer(modifier = Modifier.height(16.dp))

        // 图层选项
        LayerOption("交通状况", true)
        LayerOption("兴趣点", true)
        LayerOption("地形等高线", false)
        LayerOption("3D建筑", false)
    }
}

@Composable
fun LayerOption(name: String, isEnabled: Boolean) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(vertical = 8.dp),
        verticalAlignment = Alignment.CenterVertically,
        horizontalArrangement = Arrangement.SpaceBetween
    ) {
        Text(
            text = name,
            style = MaterialTheme.typography.bodyLarge,
            color = MaterialTheme.colorScheme.onSurface
        )

        Switch(
            checked = isEnabled,
            onCheckedChange = { /* 切换图层状态 */ }
        )
    }
}

