@Composable
fun MapScreen(){
    val configuration = LocalConfiguration.current
    configuration.screenWidthDp
    configuration.screenHeightDp
    var markers by remember { mutableStateOf<List<Point>>(emptyList()) } // 存储所有标记位置
    var markerResourceId by remember {
        mutableStateOf(R.drawable.ic_blue_marker)
    }
    var text by remember {
        mutableStateOf("Hello!")
    }

    Box(
        modifier = Modifier.fillMaxSize()
    ) {
        MapboxMap(
            modifier = Modifier.fillMaxSize(),
            mapViewportState = rememberMapViewportState {
                setCameraOptions {
                    zoom(12.3)
                    center(Point.fromLngLat(120.06022, 30.37818))
                    pitch(0.0)
                    bearing(0.0)
                }
            },
            style = { MapStyle(style = "mapbox://styles/mapbox/satellite-streets-v11") },
            onMapClickListener = { clickedPoint ->
//                        coroutineScope.launch {
//                            mapState.queryBuildingCoordinatesAt(clickedPoint)?.let {
