@Composable
fun MapScreen(onLogout: () -> Unit) {
    val configuration = LocalConfiguration.current
    val context = LocalContext.current
    configuration.screenWidthDp
    configuration.screenHeightDp
    val markers = remember { mutableStateListOf<Point>() }
    // 存储每个标记点的状态（用于拖动更新）
    val markerStates = remember { mutableStateMapOf<Int, Point>() }
    // 控制抽屉状态的变量
    var isDrawerOpen by remember { mutableStateOf(false) }
    var drawerState  = rememberDrawerState(initialValue = DrawerValue.Open)

    Box(
        modifier = Modifier.fillMaxSize()
    ) {
        MapboxMapContent(markers,markerStates)
        // 在顶部添加自定义内容
        TopToolbar(
            onLogout = onLogout,
            onClearMarkers = {
                markers.clear()
                markerStates.clear()
            },
            markerCount = markers.size,
            onSettingsClick = { isDrawerOpen = true }
        )
        Box(modifier = Modifier.width(300.dp).background(Color.White).align(Alignment.TopEnd)
            .padding(0.dp,40.dp,0.dp,0.dp)
            ,
            // 右边
            contentAlignment = Alignment.CenterEnd,
            ){
            SettingsDrawerContent()

        }

    }
    // 控制抽屉打开/关闭
    LaunchedEffect(isDrawerOpen) {
        println("isDrawerOpen: $isDrawerOpen")
        if (isDrawerOpen) {
            drawerState.open()
        } else {
            drawerState.close()
        }
    }

}



@Composable
fun MapboxMapContent(markers: MutableList<Point>,markerStates: MutableMap<Int, Point>) {
    val context = LocalContext.current
    MapboxMap(
        modifier = Modifier.fillMaxSize(),
        mapViewportState = rememberMapViewportState {
            setCameraOptions {
                zoom(12.3)
                center(Point.fromLngLat(120.06022, 30.37818))
                pitch(0.0)
                bearing(0.0)
            }
        },
        scaleBar = {
            ScaleBar(
                alignment = Alignment.BottomStart,
            )
        },
        style = { MapStyle(style = "mapbox://styles/mapbox/satellite-streets-v11") },
        onMapClickListener = { clickedPoint ->
            println("onMapClick: $clickedPoint")

            markers.add(clickedPoint)
            // 初始化标记状态
            markerStates[markers.lastIndex] = clickedPoint
            println("添加标记在: $clickedPoint")
            false
        },

        ) {

        // 1. 绘制连接所有点的折线
        if (markers.size >= 2) {
            PolylineAnnotation(
                points = markers,
            ) {
                lineColor = Color(0xffee4e8b)
                lineWidth = 5.0
            }
        }

        markers.forEachIndexed { index, point ->
            key(index) {
                AddMarker(
                    point = markerStates[index] ?: point,
                    onPointUpdated = { newPoint ->
                        // 更新点位置
                        markerStates[index] = newPoint
                        // 更新主列表（触发折线重绘）
                        markers[index] = newPoint
                    },
                    onPointClicked = {
                        // 吐司
                        Toast.makeText(context, "点击了标记点: $point", Toast.LENGTH_LONG)
                            .show()
                    }
                )
            }
        }
    }
}
// 顶部工具栏组件
@Composable
fun TopToolbar(
    onLogout: () -> Unit,
    onClearMarkers: () -> Unit,
    markerCount: Int,
    onSettingsClick: () -> Unit
) {
    Box( modifier = Modifier
        .fillMaxWidth()
        .height(40.dp)){
        Box(
            modifier = Modifier
                .fillMaxSize()
                .graphicsLayer {
                    // 应用模糊效果
                    renderEffect = BlurEffect(8f, 8f, TileMode.Decal)
                }
                // 添加一些背景颜色
                .background(Color.Black.copy(alpha = 0.3f)),
        )
        Row(
            modifier = Modifier
                .fillMaxSize()
                .padding(horizontal = 8.dp), // 添加一些内边距
            verticalAlignment = Alignment.CenterVertically,
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            // 左侧：应用标题
            Text(
                text = "地图应用",
                color = Color.White
            )

            // 中间：标记计数
            Text(
                text = "标记点: $markerCount",
                color = Color.White
            )

            // 右侧：操作按钮
                IconButton(
                    modifier = Modifier
                        .size(80.dp),
                    onClick = onSettingsClick)
                {
                    Icon(
                        painter = painterResource(R.drawable.settings_24px),
                        contentDescription = "设置",
                        tint = Color.White, // 使用 tint 参数设置图标颜色
                        modifier = Modifier.size(80.dp)
                    )
                }
        }
    }

}

// 设置抽屉内容
@Composable
fun SettingsDrawerContent(
//    onClose: () -> Unit
) {
    Column(
        modifier = Modifier
            .fillMaxHeight()
            .width(300.dp).padding(0.dp,40.dp,0.dp,0.dp),
//        horizontalAlignment = Alignment.SpaceBetween,
    ) {
        // 标题和关闭按钮
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "设置",
                style = MaterialTheme.typography.titleLarge,
                color = MaterialTheme.colorScheme.primary
            )

            IconButton(onClick = {}) {
                Icon(
                    painter = painterResource(R.drawable.close_24px),
                    contentDescription = "关闭设置",
                    tint = MaterialTheme.colorScheme.onSurface
                )
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // 设置选项
        SettingsOption(
//            icon = Icons.Default.Map,
            title = "地图类型",
            description = "选择地图显示样式"
        )

        SettingsOption(
//            icon = Icons.Default.Layers,
            title = "图层控制",
            description = "管理地图图层"
        )

        SettingsOption(
//            icon = Icons.Default.Notifications,
            title = "通知设置",
            description = "管理应用通知"
        )

        SettingsOption(
//            icon = Icons.Default.PrivacyTip,
            title = "隐私设置",
            description = "管理数据隐私选项"
        )

        Spacer(modifier = Modifier.weight(1f))

        // 底部操作按钮
        Button(
            onClick = { /* 保存设置 */ },
            modifier = Modifier.fillMaxWidth(),
            colors = ButtonDefaults.buttonColors(
                containerColor = MaterialTheme.colorScheme.primary
            )
        ) {
            Text("保存设置")
        }
    }
}

