@Composable
fun MapScreen(onLogout: () -> Unit) {
    val configuration = LocalConfiguration.current
    val context = LocalContext.current
    configuration.screenWidthDp
    configuration.screenHeightDp
    val markers = remember { mutableStateListOf<Point>() }
    // 存储每个标记点的状态（用于拖动更新）
    val markerStates = remember { mutableStateMapOf<Int, Point>() }
    // 控制抽屉状态的变量
    var isDrawerOpen by remember { mutableStateOf(false) }
    var drawerState  = rememberDrawerState(initialValue = DrawerValue.Open)
    // 动画效果
    val drawerOffset by animateDpAsState(
        targetValue = if (isDrawerOpen) 0.dp else -300.dp,
        animationSpec = tween(durationMillis = 300),
        label = "drawerAnimation"
    )
    Box(
        modifier = Modifier.fillMaxSize()
    ) {
        MapboxMapContent(markers,markerStates)
        // 在顶部添加自定义内容
        TopToolbar(
            onLogout = onLogout,
            onClearMarkers = {
                markers.clear()
                markerStates.clear()
            },
            markerCount = markers.size,
            onSettingsClick = { isDrawerOpen = true }
        )
        Box(modifier = Modifier.width(300.dp)
            .offset(x = -drawerOffset) // 从右侧滑入
            .padding(top=40.dp)
            .align(Alignment.TopEnd),
            // 右边
            contentAlignment = Alignment.CenterEnd,
            ){
            SettingsDrawerContent(
                onClose = { isDrawerOpen = false },

            )

        }

    }
    // 控制抽屉打开/关闭
    LaunchedEffect(isDrawerOpen) {
        println("isDrawerOpen: $isDrawerOpen")
        if (isDrawerOpen) {
            drawerState.open()
        } else {
            drawerState.close()
        }
    }

}



@Composable
fun MapboxMapContent(markers: MutableList<Point>,markerStates: MutableMap<Int, Point>) {
    val context = LocalContext.current
    MapboxMap(
        modifier = Modifier.fillMaxSize(),
        mapViewportState = rememberMapViewportState {
            setCameraOptions {
                zoom(12.3)
                center(Point.fromLngLat(120.06022, 30.37818))
                pitch(0.0)
                bearing(0.0)
            }
        },
        scaleBar = {
            ScaleBar(
                alignment = Alignment.BottomStart,
            )
        },
        style = { MapStyle(style = "mapbox://styles/mapbox/satellite-streets-v11") },
        onMapClickListener = { clickedPoint ->
            println("onMapClick: $clickedPoint")

            markers.add(clickedPoint)
            // 初始化标记状态
            markerStates[markers.lastIndex] = clickedPoint
            println("添加标记在: $clickedPoint")
            false
        },

        ) {

        // 1. 绘制连接所有点的折线
        if (markers.size >= 2) {
            PolylineAnnotation(
                points = markers,
            ) {
                lineColor = Color(0xffee4e8b)
                lineWidth = 5.0
            }
        }

        markers.forEachIndexed { index, point ->
            key(index) {
                AddMarker(
                    point = markerStates[index] ?: point,
                    onPointUpdated = { newPoint ->
                        // 更新点位置
                        markerStates[index] = newPoint
                        // 更新主列表（触发折线重绘）
                        markers[index] = newPoint
                    },
                    onPointClicked = {
                        // 吐司
                        Toast.makeText(context, "点击了标记点: $point", Toast.LENGTH_LONG)
                            .show()
                    }
                )
            }
        }
    }
}
// 顶部工具栏组件
@Composable
fun TopToolbar(
    onLogout: () -> Unit,
    onClearMarkers: () -> Unit,
    markerCount: Int,
    onSettingsClick: () -> Unit
) {
    Box( modifier = Modifier
        .fillMaxWidth()
        .height(40.dp)){
        Box(
            modifier = Modifier
                .fillMaxSize()
                .graphicsLayer {
                    // 应用模糊效果
                    renderEffect = BlurEffect(8f, 8f, TileMode.Decal)
                }
                // 添加一些背景颜色
                .background(Color.Black.copy(alpha = 0.3f)),
        )
        Row(
            modifier = Modifier
                .fillMaxSize()
                .padding(horizontal = 8.dp), // 添加一些内边距
            verticalAlignment = Alignment.CenterVertically,
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            // 左侧：应用标题
            Text(
                text = "地图应用",
                color = Color.White
            )

            // 中间：标记计数
            Text(
                text = "标记点: $markerCount",
                color = Color.White
            )

            // 右侧：操作按钮
                IconButton(
                    modifier = Modifier
                        .size(80.dp),
                    onClick = onSettingsClick)
                {
                    Icon(
                        painter = painterResource(R.drawable.settings_24px),
                        contentDescription = "设置",
                        tint = Color.White, // 使用 tint 参数设置图标颜色
                        modifier = Modifier.size(80.dp)
                    )
                }
        }
    }

}

// 设置抽屉内容
@Composable
fun SettingsDrawerContent(
    onClose: () -> Unit
) {
    // 添加垂直滚动支持
    val scrollState = rememberScrollState()
    var selectedSetting by remember { mutableStateOf("船速") }
    Row(
        modifier = Modifier
            .fillMaxHeight()
            .width(300.dp)
            .background(MaterialTheme.colorScheme.surface)
    ){
        Column(
            modifier = Modifier
                .fillMaxHeight()
                .width(60.dp)
                .verticalScroll(scrollState)
                .background(Color.Black.copy(alpha = 0.3f)) // 添加背景色
                .clickable(enabled = true) {} // 消费点击事件
            ,
        ) {
            val menuItems = listOf("船速", "安全", "地图", "通用", "关于", "关于应用")
            val selectedItem = remember { mutableStateOf(4) } // 假设 "关于" 是选中的

            Column {
                menuItems.forEachIndexed { index, item ->
                    SettingCategoryItem(
                        title = item,
                        isSelected = index == selectedItem.value,
                        onClick = { selectedItem.value = index
                            selectedSetting = item
                        }
                    )
                }
            }


        }
        // 分隔线
        Divider(
            modifier = Modifier
                .fillMaxHeight()
                .width(1.dp)
                .padding(vertical = 8.dp),
            color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.12f)
        )
        // 右侧：具体设置内容
        Column(
            modifier = Modifier
                .weight(1f)
                .fillMaxHeight()
                .padding(start = 16.dp)
                .verticalScroll(rememberScrollState())
        ) {
            // 标题和关闭按钮
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = selectedSetting,
                    style = MaterialTheme.typography.titleLarge,
                    color = MaterialTheme.colorScheme.primary
                )

                IconButton(onClick = onClose) {
                    Icon(
                        painter = painterResource(R.drawable.close_24px),
                        contentDescription = "关闭设置",
                        tint = MaterialTheme.colorScheme.onSurface
                    )
                }
            }

            Spacer(modifier = Modifier.height(24.dp))

            // 根据选中的设置项显示不同的内容
            when (selectedSetting) {
                "船速" -> MapTypeSettings()
                "安全" -> LayerControlSettings()
//                "地图" -> NotificationSettings()
//                "通用" -> PrivacySettings()
//                "关于" -> AccountSettings()
//                "关于应用" -> AboutAppSettings()
            }

            Spacer(modifier = Modifier.weight(1f))

            // 底部操作按钮（根据设置项变化）
            Button(
                onClick = { /* 保存设置 */ },
                modifier = Modifier.fillMaxWidth(),
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.primary
                )
            ) {
                Text("保存设置")
            }
        }
    }

}

